import React, { useState, useEffect } from 'react';
import './Appe.css'; // Import CSS module

function Product(props) {
  const [isLoading, setIsLoading] = useState(true); // Initial loading state
  const [productData, setProductData] = useState(null); // State to hold product details

  // Lifecycle method: Runs after the component renders (including initial render)
  useEffect(() => {
    const fetchProduct = async () => {
      const response = await fetch(props.productUrl); // Fetch data from prop URL
      const data = await response.json();
      setProductData(data);
      setIsLoading(false);
    };

    fetchProduct(); // Call the fetch function on component mount
  }, [props.productUrl]); // Dependency array ensures effect runs on URL change

  if (isLoading) {
    return <p>Loading product...</p>;
  }

  return (
    <div>
      <h2>{productData.name}</h2>
      <p>{productData.description}</p>
      <p>Price: ${productData.price}</p>
    </div>
  );
}

export default Product;
