import React, { useState, useEffect } from 'react';
import './e.css'; // Import CSS module

function Product(props) {
  const [isLoading, setIsLoading] = useState(true);
  const [productData, setProductData] = useState(null);

  useEffect(() => {
    const fetchProduct = async () => {
      const response = await fetch(props.productUrl);
      const data = await response.json();
      setProductData(data);
      setIsLoading(false);
    };

    fetchProduct();
  }, [props.productUrl]);

  const productStyles = isLoading ? styles.productLoading : styles.product; // Dynamic class assignment

  return (
    <div className={productStyles}>
      {isLoading ? (
        <p className={styles.loadingMessage}>Loading product...</p>
      ) : (
        <>
          <h2 className={styles.productName}>{productData.name}</h2>
          <p className={styles.productDescription}>{productData.description}</p>
          <p className={styles.productPrice}>Price: ${productData.price}</p>
          {/* Add a button for user interaction (optional) */}
          <button className={styles.productButton}>Add to Cart</button>
        </>
      )}
    </div>
  );
}

export default Product;
