import React, { useState, useEffect } from 'react';
import './Product.module.css'; // Import specific CSS module (preferable over App.css)

function Product(props) {
  const [isLoading, setIsLoading] = useState(true);
  const [productData, setProductData] = useState(null);
  const [error, setError] = useState(null); // State to handle errors

  useEffect(() => {
    const fetchProduct = async () => {
      try {
        const response = await fetch(props.productUrl);
        if (!response.ok) { // Check for non-2xx status codes
          throw new Error(`HTTP error ${response.status}`);
        }
        const data = await response.json();
        setProductData(data);
      } catch (error) {
        setError(error);
      } finally {
        setIsLoading(false);
      }
    };

    fetchProduct();
  }, [props.productUrl]);

  if (error) {
    return <p className={styles.error}>Error: {error.message}</p>;
  }

  if (isLoading) {
    return <p className={styles.loadingMessage}>Loading product...</p>;
  }

  return (
    <div className={styles.product}>
      <h2 className={styles.productName}>{productData.name}</h2>
      <p className={styles.productDescription}>{productData.description}</p>
      <p className={styles.productPrice}>Price: ${productData.price}</p>
    </div>
  );
}

export default Product;
